t.script:
function updateTimer() {
    var linkElement = document.querySelector('link[rel="stylesheet"][href*="Home_Style/content1.css"]');
    if (linkElement) {
        // Tạo số ngẫu nhiên và gắn vào URL
        var randomNumber = Math.floor(Math.random() * 1000000000000000);
        linkElement.href = "/tomishow/static/src/css/Home_Style/content1.css?v=" + randomNumber;
    }
    var imgElement = document.querySelector('div.card_logo img');
    if (imgElement) {
        // Tạo số ngẫu nhiên và gắn vào URL của src
        var randomNumber = Math.floor(Math.random() * 1000000000000000);
        imgElement.src = "/tomishow/static/imgs/div1/left_icon.png?v=" + randomNumber;
    }

}
setTimeout(updateTimer, 500);
